    public void ReadDataBase() {
        int i = 0;
        int rn = DriversTable.getRowCount();
        DefaultTableModel table = (DefaultTableModel) DriversTable.getModel();
        while (i < rn) {
            table.removeRow(0);
            i++;
        }
        try {
            Statement st;
            st = con.createStatement();
            ResultSet rs = st.executeQuery("SELECT * FROM driver;");
            while (rs.next()) {
                String Row[] = new String[]{rs.getString("IdDriver"), rs.getString("Name") + " " + rs.getString("LastName"),
                    rs.getString("Address"), rs.getString("PhoneNumber"), rs.getString("BirthDate"), rs.getString("Salary")};
                table.addRow(Row);
            }
        } catch (SQLException ex) {
        }
    }

