    private void ModifyButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ModifyButtonActionPerformed
        int i = DriversTable.getSelectedRow();
        if (i == -1)
            JOptionPane.showMessageDialog(null, "No has seleccionado nada", "Error", JOptionPane.ERROR_MESSAGE);
        else {
            try {
                int id = Integer.parseInt(DriversTable.getValueAt(i, 0).toString());
                Statement st;
                st = con.createStatement();
                st.executeUpdate("UPDATE driver SET Name='" + NameTextField.getText() + "', LastName='"
                        + LastNameTextField.getText() + "', Address='" + AddressTextField.getText() + "', PhoneNumber="
                        + PhoneNumberTextField.getText() + ", BirthDate='" + BirthDateTextField.getText()
                        + "', Salary=" + SalaryTextField.getText() + " WHERE IdDriver=" + id);
                ReadDataBase();
                JOptionPane.showMessageDialog(null, "Se Actualizaron los datos del conductor", "Listo", JOptionPane.INFORMATION_MESSAGE);
            } catch (SQLException ex) {
            }
        }
    }//GEN-LAST:event_ModifyButtonActionPerformed

    private void DeleteButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_DeleteButtonActionPerformed
        int i = DriversTable.getSelectedRow();
        if (i == -1)
            JOptionPane.showMessageDialog(null, "No has seleccionado ningun conductor", "Error", JOptionPane.ERROR_MESSAGE);
        else {
            try {
                int id = Integer.parseInt(DriversTable.getValueAt(i, 0).toString());
                Statement st;
                st = con.createStatement();
                st.executeUpdate("DELETE FROM driver WHERE IdDriver=" + id);
                DefaultTableModel table = (DefaultTableModel) DriversTable.getModel();
                table.removeRow(i);
                JOptionPane.showMessageDialog(null, "Se elimino el conductor de la lista", "Listo", JOptionPane.INFORMATION_MESSAGE);
            } catch (SQLException ex) {
            }
        }
    }//GEN-LAST:event_DeleteButtonActionPerformed

    private void AddButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_AddButtonActionPerformed
        try {
            boolean exists = false;
            String comp = "";
            String driver = NameTextField.getText() + " " + LastNameTextField.getText();
            driver = driver.toLowerCase();

            Statement stR = con.createStatement();
            ResultSet rsR = stR.executeQuery("SELECT * FROM driver");

            while (rsR.next()) {
                String A = rsR.getString("Name");
                String B = rsR.getString("LastName");
                comp = A + " " + B;
                comp = comp.toLowerCase();
                if (comp.equalsIgnoreCase(driver)) {
                    exists = true;
                }
            }
            stR.close();

            if (exists == true) {
                JOptionPane.showMessageDialog(null, "Ya existe un conductor del mismo nombre\n"
                        + "dentro del sistema.", "Error", JOptionPane.ERROR_MESSAGE);
            } else {
                Statement st = con.createStatement();
                if ("".equals(NameTextField.getText()) || "".equals(NameTextField.getText())
                        || "".equals(AddressTextField.getText())) {
                    JOptionPane.showMessageDialog(null, "Todos los campos son obligatorios", "Error", JOptionPane.ERROR_MESSAGE);
                } else {
                    st.execute("INSERT INTO `driver` (`IdDriver`, `Name`, `LastName`, `Address`, `PhoneNumber`, `BirthDate`, `Salary`)"
                            + "VALUES (NULL,'" + NameTextField.getText() + "','" + LastNameTextField.getText() + "','"
                            + AddressTextField.getText() + "'," + PhoneNumberTextField.getText() + ",'"
                            + BirthDateTextField.getText() + "'," + SalaryTextField.getText() + ")");
                    JOptionPane.showMessageDialog(null, "El conductor se ha añadido a la lista   ", "Listo", JOptionPane.INFORMATION_MESSAGE);
                    ReadDataBase();
                }
            }
        } catch (SQLException ex) {
        }
    }//GEN-LAST:event_AddButtonActionPerformed

